INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Frogskins MIX', 2024, 'Feminino', 4.7, 'https://www.oakley.com/pt-br/product/W0OO9428?variant=888392640512', 'Inspirado em terrenos acidentados e aventuras outdoor, o Terraform entrega robustez e estilo moderno.','A estrutura reforçada garante durabilidade extrema. O encaixe anatômico e as lentes Prizm™ Trail oferecem nitidez superior em ambientes naturais e trilhas.',550.00, 216.90);
