INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Gibston', 2025, 'Masculino', 4.6, 'https://www.oakley.com/pt-br/product/W0OO9449?variant=888392640732', 'Design ultraleve inspirado em correntes de ar e fluidez.', 'As hastes finas e flexíveis em O-Matter™ proporcionam conforto durante todo o dia. Lentes Prizm™ Rose Gold aprimoram os detalhes e realçam cores quentes em ambientes urbanos e naturais.',791.00, 282.30);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Holbrook XL', 2023, 'Masculino', 4.5, 'https://www.oakley.com/pt-br/product/W0OO9417?variant=888392639988', 'Um clássico reinterpretado com tecnologia moderna.', 'Com estrutura reforçada e ponte ajustável em três níveis, o Spectrum RX é compatível com lentes de grau e oferece transição perfeita entre estilo de rua e esportividade.',560.00, 199.90);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Pitchman R', 2025, 'Unissex', 4.9, 'https://www.oakley.com/pt-br/product/W0OO9439?variant=888392640899', 'Inspirado nas cores do cosmos e na velocidade da luz.', 'Com lentes espelhadas Iridium™ e design geométrico futurista, o Nebula Shift combina performance e atitude. Ideal para quem busca diferenciação e proteção de alto nível.',1150.00, 410.60);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Forager', 2024, 'Unissex', 4.7, 'https://www.oakley.com/pt-br/product/W0OO9421?variant=888392641554', 'Minimalista e resistente, o Crux Zero oferece o equilíbrio perfeito entre leveza e estrutura.', 'As hastes de titânio escovado proporcionam conforto e rigidez. As lentes Prizm™ Deep Water reduzem reflexos e aumentam a clareza visual em ambientes náuticos.',350.00, 124.90);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('HSTN - Frame', 2025, 'Unissex', 4.8, 'https://www.oakley.com/pt-br/product/W0OX8139?variant=888392604743', 'Com curvas suaves e acabamento translúcido, o Solaris Frame expressa leveza e autenticidade.', 'A lente Prizm™ Bronze aumenta o contraste e valoriza tons terrosos. Perfeito para uso urbano, o modelo alia moda e tecnologia em um só design.',1050.00, 410.60);
