INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Radar EV Advancer', 2025, 'Feminino', 4.9, 'https://www.oakley.com/pt-br/product/W0OO9442?variant=888392642120', 'Design leve e elegante para quem busca estilo e performance.', 'As lentes arredondadas com Prizm™ Black Polarized reduzem o brilho e realçam os tons naturais. Ideal para o dia a dia, o Lyra Edge combina sofisticação e tecnologia com um ajuste preciso e confortável.',2016.00, 611.17);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Frogskins MIX', 2024, 'Feminino', 4.7, 'https://www.oakley.com/pt-br/product/W0OO9428?variant=888392640512', 'Inspirado em terrenos acidentados e aventuras outdoor, o Terraform entrega robustez e estilo moderno.','A estrutura reforçada garante durabilidade extrema. O encaixe anatômico e as lentes Prizm™ Trail oferecem nitidez superior em ambientes naturais e trilhas.',550.00, 216.90);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Tailend TI', 2025, 'Feminino', 5.0,'https://www.oakley.com/pt-br/product/W0OO6026?variant=888392674975', 'Agradável aos olhos, com um toque leve. Conheça o Tailend TI: o icônico modelo Tailend, reinventado. Sua estrutura de titânio sem aro faz você sentir como se ele fosse quase leve, mas cara, ele é muito pesado em estilo.', 'Agradável aos olhos, com um toque leve. Conheça o Tailend TI: o icônico modelo Tailend, reinventado. Sua estrutura de titânio sem aro faz você sentir como se ele fosse quase leve, mas cara, ele é muito pesado em estilo.', 2200.00, 1500.00);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Permian', 2024, 'Feminino', 4.5, 'https://www.oakley.com/pt-br/product/W0OO9520?variant=888392675996', 'Eleve seu visual com um toque de requinte. Inspirado nos looks fora de campo dos principais atletas, o Permian é uma peça de destaque com uma armação elegante e lentes de base bases para o ajuste e conforto máximos.', 'Eleve seu visual com um toque de requinte. Inspirado nos looks fora de campo dos principais atletas, o Permian é uma peça de destaque com uma armação elegante e lentes de base bases para o ajuste e conforto máximos, Armação de O-Matter, Design Envolvente e Logotipo OAKLEY Fractal', 1100.00, 850.00);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Oakley Kato', 2025, 'Masculino', 5.0, 'https://www.oakley.com/pt-br/product/W0OO9455?variant=888392643205', 'Modelo esportivo de alto desempenho com design envolvente.', 'A curvatura ampla proporciona cobertura total e estabilidade durante o movimento. As lentes Prizm™ Sapphire garantem proteção total contra UV e visão impecável mesmo sob sol intenso.',999.00, 411.40);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Gibston', 2025, 'Masculino', 4.6, 'https://www.oakley.com/pt-br/product/W0OO9449?variant=888392640732', 'Design ultraleve inspirado em correntes de ar e fluidez.', 'As hastes finas e flexíveis em O-Matter™ proporcionam conforto durante todo o dia. Lentes Prizm™ Rose Gold aprimoram os detalhes e realçam cores quentes em ambientes urbanos e naturais.',791.00, 282.30);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Holbrook XL', 2023, 'Masculino', 4.5, 'https://www.oakley.com/pt-br/product/W0OO9417?variant=888392639988', 'Um clássico reinterpretado com tecnologia moderna.', 'Com estrutura reforçada e ponte ajustável em três níveis, o Spectrum RX é compatível com lentes de grau e oferece transição perfeita entre estilo de rua e esportividade.',560.00, 199.90);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Plantaris Polaris Collection', 2025, 'Masculino', 5.0, 'https://www.oakley.com/pt-br/product/W0OO9437PSC?variant=888392675040', 'Polaris é a Estrela do Norte que você pode seguir para expressar sua personalidade única, com a confiança de avançar para uma nova fronteira. A coleção sob medida da Oakley apresenta novos tratamentos de pintura Abyss Galaxy e Fern Galaxy.', 'Inspirado pela observação das formas e criaturas encontradas na natureza, o Plantaris tem um design ousado com características funcionais e técnicas. O design de haste ajustável se divide em duas bases inspiradas em sapos, cobertas por pontas de silicone transparentes exibindo o núcleo de arame que remete a veias. Uma ponta cônica removível torna-o adaptável ao humor e ao ambiente em que você estiver, enquanto a tecnologia Switchlock facilita a troca das lentes. Para cores e contrastes aprimorados em todos os lugares que você vai, a Tecnologia de Lentes Prizm™ fornece os detalhes.', 1700.00, 950.00);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Oakley X Satisfy Stunt Devil', 2025, 'Masclino', 5.0, 'https://www.oakley.com/pt-br/product/W0OO9517STY?variant=888392682956', 'Stunt Devil é o melhor modelo de óculos esportivo, proporcionando retenção, aderência e conforto sobrenaturais em cada salto e corrida.', 'Stunt Devil é o melhor modelo de óculos esportivo, proporcionando retenção, aderência e conforto sobrenaturais em cada salto e corrida. Trabalhando ao lado da Satisfy® Running, essas iterações únicas combinam tecnologia revolucionária e estética distinta, permitindo que os usuários se aventurem fora do caminho tradicional.', 1500.00, 900.00);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Pitchman R', 2025, 'Unissex', 4.9, 'https://www.oakley.com/pt-br/product/W0OO9439?variant=888392640899', 'Inspirado nas cores do cosmos e na velocidade da luz.', 'Com lentes espelhadas Iridium™ e design geométrico futurista, o Nebula Shift combina performance e atitude. Ideal para quem busca diferenciação e proteção de alto nível.',1150.00, 410.60);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Forager', 2024, 'Unissex', 4.7, 'https://www.oakley.com/pt-br/product/W0OO9421?variant=888392641554', 'Minimalista e resistente, o Crux Zero oferece o equilíbrio perfeito entre leveza e estrutura.', 'As hastes de titânio escovado proporcionam conforto e rigidez. As lentes Prizm™ Deep Water reduzem reflexos e aumentam a clareza visual em ambientes náuticos.',350.00, 124.90);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('HSTN - Frame', 2025, 'Unissex', 4.8, 'https://www.oakley.com/pt-br/product/W0OX8139?variant=888392604743', 'Com curvas suaves e acabamento translúcido, o Solaris Frame expressa leveza e autenticidade.', 'A lente Prizm™ Bronze aumenta o contraste e valoriza tons terrosos. Perfeito para uso urbano, o modelo alia moda e tecnologia em um só design.',1050.00, 410.60);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('Sutro S', 2025, 'Unissex', 5.0, 'https://www.oakley.com/pt-br/product/W0OO9462?variant=888392643740', 'Design futurista inspirado no universo digital.', 'As linhas retas e a moldura em acetato reforçado garantem um visual moderno e marcante. Com lentes Prizm™ Black, o Cyberline entrega nitidez e conforto visual em qualquer ambiente.', 1050.00, 410.60);
INSERT INTO tb_oculos (name, oculos_year, genre, review_score, img_url, short_description, long_description, price, cost_price) VALUES ('De Soto Polaris', 2024, 'Unissex', 5.0, 'https://www.oakley.com/pt-br/product/W0OO9494PSC?variant=888392675071' , 'Polaris é a Estrela do Norte que você pode seguir para expressar sua personalidade única, com a confiança de avançar para uma nova fronteira.', 'Esteja pronto para o carpete vermelho com o De Soto, um óculos de sol arrojado impulsionado por um design igualmente arrojado. Parte da Coleção Players, o De Soto foi inspirado nas silhuetas dos anos 60, com um toque novo e distinto da Oakley®. Com uma lente dupla espessa e marcante, material de armação O Matter™ durável e leve e o exclusivo logotipo Oakley® Fractal na haste, o De Soto oferece estilo infinito para aquela foto pré-jogo ou comemoração pós-jogo. Equipado com a Tecnologia de Lentes Prizm™ que realça cores e contraste, o De Soto vem com microbag e caixa personalizadas.', 960.00, 350.50 );
